sys_script_include.script:
var GetTeam12 = Class.create();
GetTeam12.prototype = {
    initialize: function() {
    },
	GetTeam: function()
	{
	gs.info("id:- "+gs.getUserID());
	var rec = new GlideRecord('x_568702_birthday_birthday');
	rec.addQuery('u_name', gs.getUserID());
	rec.query();
	var team = [];
	while(rec.next())
		{
			team.push( rec.getValue('u_team') );
		}
	gs.info("team:- "+team);
	return team;
	},

    type: 'GetTeam12'
};
